(9334, 1, '1994-10-22 00:00:00', '1994-10-22 00:00:00', '\r<p>День без событий.</p>\r', '', '', 'publish', 'closed', 'closed', '', '9334', '', '', '1994-10-22 00:00:00', '1994-10-22 00:00:00', '', 0, 'http://localhost/dbwp/1994/10/22/9334/', 0, 'post', '', 0),
(9335, 1, '1994-10-23 00:00:00', '1994-10-23 00:00:00', '\r<p>Мама и папа уехали на работу.</p>\r', '', '', 'publish', 'closed', 'closed', '', '9335', '', '', '1994-10-23 00:00:00', '1994-10-23 00:00:00', '', 0, 'http://localhost/dbwp/1994/10/23/9335/', 0, 'post', '', 0),
